mapper.select:
SELECT nvl(sum(amount),0) as amount 
FROM cart
WHERE product_detail_no = #{productDetailNo} and psize = #{psize} and member_id = #{memberId}

mapper.update:
UPDATE cart
SET amount = #{amount}
WHERE product_detail_no = #{productDetailNo} and psize = #{psize} and member_id = #{memberId}

UPDATE cart
SET product_detail_no = #{newProductDetailNo}, psize = #{newPsize}
WHERE product_detail_no = #{productDetailNo} and psize = #{psize} and member_id = #{memberId}
